function getUrlParams(url_string, key) {
    var url = new URL(url_string);
    var c = url.searchParams.get(key);
    // console.log(key, c);
    return c;
}


var http = getUrlParams(url, "http");
var ServerLocation = getUrlParams(url, "ServerLocation");
var extraPathSelected = getUrlParams(url, "extraPathSelected");

extraPath = "";
if (extraPathSelected != null) {
    if (extraPathSelected.indexOf("MAIN") < 0) {
        LFolder = extraPathSelected;
        extraPath = extraPathSelected + "/";
    }
}
