+  var subtotalGROSSAMT = 0;
+  var subtotalGOODSRETURN = 0;
+  var subtotalPAIDAMT = 0;
+  var subtotalAmount = 0;
+  var BLL;
+  var FdataUrl
+  var DL = Data.length;
+  tr = "<tbody>";
+
+  var DtArray = [];
