+<html>
+
+<head>
+
+  <title>BILL</title>
+  <link rel="stylesheet" type="text/css" href="css/billPdf.css">
+  <script src="3.3.1/jquery.min.js"></script>
+  <script src="3.3.7/bootstrap.min.js"></script>
+  <script src="js/jsGetUrlParams.js"></script>
